import { useEffect } from "react";
import { Routes, Route, useNavigationType, useLocation } from "react-router-dom";
import Homepage from "./pages/Homepage";
import Appointment from "./pages/Appointment";
import AppointmentConfirmation from "./pages/AppointmentConfirmation"; // Import the AppointmentConfirmation component
import ContactUs from "./pages/ContactUs";
import Lawyer from "./pages/Lawyer";
import Search from "./pages/Search"; // Import the Search component
import Footer from "./components/Footer"; // Import the Footer component
import FrameComponent10 from "./components/FrameComponent10"; // Import the navigation component
import { LanguageProvider, useLanguage } from './LanguageContext'; // Import the Language context

function App() {
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;
  const { language } = useLanguage(); // Get the current language from context

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    switch (pathname) {
      case "/":
        title = language === 'en' ? "Homepage" : "Page d'accueil";
        metaDescription = language === 'en' ? "Welcome to the homepage." : "Bienvenue sur la page d'accueil.";
        break;
      case "/appointment":
        title = language === 'en' ? "Appointment" : "Rendez-vous";
        metaDescription = language === 'en' ? "Book an appointment." : "Prenez un rendez-vous.";
        break;
      case "/appointment-confirmation":
        title = language === 'en' ? "Appointment Confirmation" : "Confirmation de rendez-vous";
        metaDescription = language === 'en' ? "Your appointment is confirmed." : "Votre rendez-vous est confirmé.";
        break;
      case "/contact-us":
        title = language === 'en' ? "Contact Us" : "Contactez-nous";
        metaDescription = language === 'en' ? "Get in touch with us." : "Contactez-nous.";
        break;
      case "/lawyer":
        title = language === 'en' ? "Lawyer" : "Avocat";
        metaDescription = language === 'en' ? "Meet our lawyers." : "Rencontrez nos avocats.";
        break;
      case "/search":
        title = language === 'en' ? "Search" : "Recherche";
        metaDescription = language === 'en' ? "Search our site." : "Recherchez sur notre site.";
        break;
      default:
        break;
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector('head > meta[name="description"]');
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname, language]);

  return (
    <LanguageProvider>
      <div className="app">
        <FrameComponent10 /> {/* Add the navigation component here */}
        <Routes>
          <Route path="/" element={<Homepage />} />
          <Route path="/appointment" element={<Appointment />} />
          <Route path="/appointment-confirmation" element={<AppointmentConfirmation />} /> {/* Add the AppointmentConfirmation route here */}
          <Route path="/contact-us" element={<ContactUs />} />
          <Route path="/lawyer" element={<Lawyer />} />
          <Route path="/search" element={<Search />} /> {/* Add the Search route here */}
        </Routes>
        <Footer /> {/* Add the Footer component here */}
      </div>
    </LanguageProvider>
  );
}

export default App;
